mapper.update:
update sky_take_out.orders
status = #{status}, pay_status = #{payStatus}, checkout_time = #{checkoutTime},
where number = #{number}

mapper.select:
select * from sky_take_out.orders


        number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount) from sky_take_out.orders

    order_time > #{begin}
     and order_time < #{end}
     and status = #{status}

select count(id) from sky_take_out.orders

    order_time > #{begin}
     and order_time < #{end}
     and status = #{status}
