mapper.update:
update sky_take_out.setmeal
category_id = #{categoryId} , description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status} ,
where id = #{id};

mapper.select:
select s.*,c.name as categoryName from sky_take_out.setmeal as s left join sky_take_out.category as c on s.category_id = c.id

 s.name = #{name} 
 s.categoryId = #{categoryId} 
 s.status = #{status}

select * from sky_take_out.setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
