mapper.update:
update star_food_chain.dish
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status},
where id = #{id}

mapper.select:
select count(id) from star_food_chain.dish


        and status = #{status}


        and category_id = #{categoryId}

mapper.delete:
#         delete from dish where id in (???)
    delete from star_food_chain.dish where id in

            #{id}

mapper.select:
select d.*, c.name as categoryName from star_food_chain.dish as d left join star_food_chain.category as c on d.category_id = c.id

     d.category_id = #{categoryId}
     d.name like concat('%',#{name},'%')
     d.status = #{status}

order by d.create_time desc

select * from star_food_chain.dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
