mapper.update:
update star_food_chain.setmeal
category_id = #{categoryId} , description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status} ,
where id = #{id};

mapper.select:
select count(id) from star_food_chain.setmeal


        and status = #{status}


        and category_id = #{categoryId}

select s.*,c.name as categoryName from star_food_chain.setmeal as s left join star_food_chain.category as c on s.category_id = c.id

 s.name = #{name} 
 s.categoryId = #{categoryId} 
 s.status = #{status}

select * from star_food_chain.setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
